import Foundation

public extension CoinGecko {
    struct NFT: Codable, Identifiable {
        public var id: String
        public var name: String?
        
        static public func get() async -> [NFT] {
            
            if let nfts: [NFT] = try? await CoinGecko.nfts(order: nil, assetPlatformId: nil, perPage: nil, page: nil).getObject() {
                return nfts
            } else {
                return []
            }

            return []
        }

    }
}
